    --check-mark: transparent;
    --check-bgr: transparent;
    --check-border: var(--base-color4);

    }
  }
  // ============ Тэги в плашке товара =============
  &-btn-plus { 
    --vertical-line: currentColor;
    &.minus {
      --vertical-line: transparent;
    }
   }
