    color: #fff;
    &-ya-go{
      --go: #fff;
      @include icon-size(165, 720);
    }
    &-dostavista {
      color: #FF3E80!important;
      @include icon-size(165, 720);
    }
    &-cdek {
      @include icon-size(165, 720);
    }
  }
  
  &-location {
    --location-stroke: currentColor;
    --location-bgr: transparent;
    --location-dot: transparent; 
    --location-dot-stroke: currentColor; 

  }
  &-jsb {@include icon-size(70, 90);}
  &-mastercard {@include icon-size(70, 90);}
  &-mir {@include icon-size(70, 90);}
  &-visa {@include icon-size(70, 90);}
  &-yoomoney {
